def resource_path(relative_path):
    try:
        base_path = sys._MEIPASS
    except Exception:
        base_path = os.path.abspath(".")
    return os.path.join(base_path, relative_path)

class CircularImageLabel(QLabel):
    def __init__(self, pixmap, border_color, parent=None):
        super().__init__(parent)
        self.pixmap = pixmap
        self.border_color = border_color
        self.setFixedSize(100, 100)

    def paintEvent(self, event):
        painter = QPainter(self)
        painter.setRenderHint(QPainter.RenderHint.Antialiasing)
        border_width = 3
        full_rect = QRectF(self.rect())
        painter.setPen(Qt.PenStyle.NoPen)
        painter.setBrush(QBrush(self.border_color))
        painter.drawEllipse(full_rect)
        image_rect = full_rect.adjusted(border_width, border_width, -border_width, -border_width)
        image_path = QPainterPath()
        image_path.addEllipse(image_rect)
        painter.setClipPath(image_path)
        scaled_pixmap = self.pixmap.scaled(self.size(), Qt.AspectRatioMode.KeepAspectRatioByExpanding, Qt.TransformationMode.SmoothTransformation)
        painter.drawPixmap(self.rect(), scaled_pixmap)

